import * as actionTypes from './actionTypes';
import axios from 'axios';
import ability from '../../shared/ability';

export const authStart = () => {
  return {
    type: actionTypes.AUTH_START
  }
}

export const authSuccess = (userId, username, token) => {
  return {
    type: actionTypes.AUTH_SUCCESS,
    userId: userId,
    username: username,
    token: token
  }
}

export const authFail = (error) => {
  return {
    type: actionTypes.AUTH_FAIL,
    error: error
  }
}

export const logout = () => {
  sessionStorage.removeItem('token');
  localStorage.removeItem('username');
  localStorage.removeItem('userId');

  return {
    type: actionTypes.AUTH_LOGOUT
  }
}

export const auth = (username, password) => {
  return dispatch => {
    dispatch(authStart());

    const headers = {
      Authorization: `Basic ${ sessionStorage.getItem('token') }, Bearer eyJhbGciOiJIUzUxMiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJwWnN1UkhHdE5JZEUxUWZmazJQOENJMk1RdFdLWGl6OSJ9.B6IHmiuCjnx_zGJUVQI_ib_Scm88jHbYS1afaFS7PZ23WNcgOMoO12BkYdOuIbVAR8hOH2CB5G5Itnl7U77P3w`,
    }

    const authData = {
      username: username,
      password: password,
      grant_type: 'password',
      client_id: process.env.CLIENT_ID,
      client_secret: process.env.CLIENT_SECRET,
    }

    let url = 'http://localhost:8000/oauth/token';

    axios.post(url, authData, { headers: headers })
      .then(response => {
        sessionStorage.setItem('token', response.data.access_token);
        localStorage.setItem('username', response.data.username);
        localStorage.setItem('userId', response.data.userId);
        console.log(response)
        ability.update(response.data.rules);
        dispatch(authSuccess(
          response.data.userId, 
          response.data.username, 
          response.data.access_token
        ));
      })
      .catch(err => {
        dispatch(authFail(err));
      })
  }
}
