import React, { Component } from 'react';
import { connect } from 'react-redux';
import Router from 'next/router'
import axios from '../../services/axios/axios-user';

import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import { checkValidity, updateObject } from '../../shared/utility';
import * as actions from '../../store/actions/index';

class Update extends Component {
  state = {
    controls: {
      current_password: {
        elementType: 'input',
        elementConfig: {
          type: 'password',
          placeholder: 'Current Password'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
      password: {
        elementType: 'input',
        elementConfig: {
          type: 'password',
          placeholder: 'New Password'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
      password_confirmation: {
        elementType: 'input',
        elementConfig: {
          type: 'password',
          placeholder: 'Password Confirmation'
        },
        value: '',
        validation: {
          required: true,
          minLength: 6
        },
        valid: false,
        touched: false
      }
    },
  }

  inputChangedHandler = (event, controlName) => {
    const updatedControls = updateObject(this.state.controls, {
      [controlName]: updateObject(this.state.controls[controlName], {
        value: event.target.value,
        valid: checkValidity(event.target.value, this.state.controls[controlName].validation),
        touched: true
      })
    });

    this.setState({ controls: updatedControls });
  }

  submitHandler = (event) => {
    event.preventDefault();
    this.props.onUpdate(
      localStorage.getItem('userId'),
      this.state.controls.password.value, 
      this.state.controls.password_confirmation.value
    )
      .then(() => {
        Router.push('/index')
      })
  }

  render () {
    const formElementsArray = []
    for (let key in this.state.controls) {
      formElementsArray.push({
        id: key,
        config: this.state.controls[key]
      })
    }

    let form = formElementsArray.map(formElement => (
      <Input
        key={ formElement.id }
        elementType={ formElement.config.elementType }
        elementConfig={ formElement.config.elementConfig }
        value={ formElement.config.value }
        invalid={ !formElement.config.valid }
        shouldValidate={ formElement.config.validation }
        touched={ formElement.config.touched }
        changed={ (event) => this.inputChangedHandler(event,formElement.id) }
      />
    ));

    if (this.props.loading) {
      form = <CircularProgress />;
    }

    let errorMessage = null;

    if (this.props.error) {
      errorMessage = (
        <p>{ this.props.error }</p>
      );
    }

    return (
      <div>
        <h1>Update Password</h1>

        { errorMessage }
        <form onSubmit={ this.submitHandler }>
          { form }
          <Button>Submit</Button>
        </form>
      </div>
    )
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onUpdate: (username, current_password, password, password_confirmation) => dispatch(actions.update(
      username,
      current_password,
      password,
      password_confirmation
    ))
  }
}

export default connect(null, mapDispatchToProps)(Update);
