import * as actionTypes from './actionTypes';
import axios from '../../services/axios/axios-user';
import ability from '../../shared/ability';

export const authStart = () => {
  return {
    type: actionTypes.AUTH_START
  }
}

export const authSuccess = (userId, username) => {
  return {
    type: actionTypes.AUTH_SUCCESS,
    userId: userId,
    username: username,
  }
}

export const authFail = (error) => {
  return {
    type: actionTypes.AUTH_FAIL,
    error: error
  }
}

export const logout = () => dispatch => new Promise((resolve, reject) => { 
  const url = '/oauth/revoke';

  resolve(
    axios.post(url)
      .then(response => {
        sessionStorage.removeItem('token');
        localStorage.removeItem('username');
        localStorage.removeItem('userId');

        return {
          type: actionTypes.AUTH_LOGOUT
        }
      })
  );
});

export const auth = (username, password) => {
  return dispatch => {
    dispatch(authStart());

    const authData = {
      username: username,
      password: password,
      grant_type: 'password',
      client_id: process.env.CLIENT_ID,
      client_secret: process.env.CLIENT_SECRET,
    }

    let url = '/oauth/token';

    axios.post(url, authData)
      .then(response => {
        sessionStorage.setItem('token', response.data.access_token);
        localStorage.setItem('username', response.data.username);
        localStorage.setItem('userId', response.data.userId);
        console.log(response)
        ability.update(response.data.rules);
        dispatch(authSuccess(
          response.data.userId, 
          response.data.username, 
        ));
      })
      .catch(err => {
        const errorData = [];
        const error = {};
        console.log('auth error', err.response)
        Object.entries(err.response.data.errors).forEach(([key, value]) => {
          /* eslint prefer-destructuring: ["error", {VariableDeclarator: {object: true}}] */
          error[key] = value[0];
          errorData.push(error)
        });

        dispatch(authFail(errorData[0]));
      })
  }
}
