import React, { Component } from 'react';
import Link from 'next/link'
import { connect } from 'react-redux';
import ability from '../../shared/ability';

import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/Button';
import CircularProgress from '@material-ui/core/CircularProgress';
import { updateObject, checkValidity } from '../../shared/utility';
import * as actions from '../../store/actions/index';

class Auth extends Component {
  state = {
    controls: {
      username: {
        elementType: 'input',
        elementConfig: {
          type: 'text',
          placeholder: 'Username'
        },
        value: '',
        validation: {
          required: true,
        },
        valid: false,
        touched: false
      },
      password: {
        elementType: 'input',
        elementConfig: {
          type: 'password',
          placeholder: 'Password'
        },
        value: '',
        validation: {
          required: true,
          minLength: 6
        },
        valid: false,
        touched: false
      }
    },
  }

  componentDidMount = () => {
    this.userId = localStorage.getItem('userId');
    this.username = localStorage.getItem('username');
  }

  inputChangedHandler = (event, controlName) => {
    const updatedControls = updateObject(this.state.controls, {
      [controlName]: updateObject(this.state.controls[controlName], {
        value: event.target.value,
        valid: checkValidity(event.target.value, this.state.controls[controlName].validation),
        touched: true
      })
    });

    this.setState({ controls: updatedControls });
  }

  submitHandler = (event) => {
    event.preventDefault();
    this.props.onAuth(this.state.controls.username.value, this.state.controls.password.value);
  }

  render () {

    const formElementsArray = []
    for (let key in this.state.controls) {
      formElementsArray.push({
        id: key,
        config: this.state.controls[key]
      })
    }

    let form = formElementsArray.map(formElement => (
      <Input
        key={ formElement.id }
        elementType={ formElement.config.elementType }
        elementConfig={ formElement.config.elementConfig }
        value={ formElement.config.value }
        invalid={ !formElement.config.valid }
        shouldValidate={ formElement.config.validation }
        touched={ formElement.config.touched }
        changed={ (event) => this.inputChangedHandler(event,formElement.id) }
      />
    ));

    if (this.props.loading) {
      form = <CircularProgress />;
    }

    let errorMessage = null;

    if (this.props.error) {
      errorMessage = (
        <p>{ this.props.error }</p>
      );
    }

    let abilities = ability.rules.map((rule, index) => (
        <label key={ index } style={{ display: 'block' }}>
          rules [{ rule.actions }: { rule.subject }]
        </label>
      )
    );

    return (
      <div>
        <h1>Auth</h1>

        <Link href='/index'>
          <a>Home</a>
        </Link>

        <Link href={ `/user/show?slug=${ this.props.username }` } as={ `/user/${ this.props.username }` }>
          <a>My Profile</a>
        </Link>

        <Link href='/auth/logout' as='/logout'>
          <a>Logout</a>
        </Link>

        <div>
          { errorMessage }
          <form onSubmit={ this.submitHandler }>
            { form }
            <Button>Submit</Button>
          </form>
        </div>

        <div>
          <label style={{ display: 'block' }}>ID: { this.userId }</label>
          <label style={{ display: 'block' }}>Username: { this.username }</label>
          { abilities }
        </div>
      </div>
    )
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onAuth: (username, password) => dispatch(actions.auth(username, password)),
  }
}

export default connect(null, mapDispatchToProps)(Auth);
