import * as actionTypes from './actionTypes';
import axios from '../../services/axios/axios-user';

export const userShow = (username) => {
  return {
    type: actionTypes.USER_SHOW,
    username: username
  }
}

export const show = (username) => dispatch => new Promise((resolve, reject) => {
  const url = `/api/users/${ username }`

  resolve(
    axios.get(url)
      .then(response => {
        console.log('show user', response)
        // return response.data
        dispatch(userShow(response.data.username));
      })
  )
})

export const update = (username, password, password_confirmation) => dispatch => new Promise((resolve, reject) => {
  const url = `/api/users/${ username }`
  const userData = {
    password: password,
    password_confirmation: password_confirmation,
  }

  resolve(
    axios.patch(url, userData)
      .then(response => {
        console.log('update response', response)
      })
  )
})
