import * as actionTypes from './actionTypes';
import axios from '../../shared/userAxios';

const registerStart = () => {
  return {
    type: actionTypes.REGISTER_START,
  }
}

const registerSuccess = (loading) => {
  return {
    type: actionTypes.REGISTER_SUCCESS,
    loading: loading,
  }
}

const registerError = (error) => {
  return {
    type: actionTypes.REGISTER_FAIL,
    error: error,
  }
}

const register = (username, password, password_confirmation) => {
  return dispatch => {
    dispatch(registerStart());

    url = '/api/users';

    registerData = {
      username: username,
      password: password,
      password_confirmation: password,
    }

